mapper.select:
WITH ORDEREDBRD AS ( SELECT A.*, ROWNUM RNUM FROM ( SELECT EVNT_NO, EVNT_TITLE, EVNT_CONTENT, EVNT_LNO , TO_CHAR(RGST_DATE, 'YYYY.MM.DD') AS RGST_DATE , TO_CHAR(START_DATE, 'YYYY.MM.DD') AS START_DATE , TO_CHAR(END_DATE, 'YYYY.MM.DD') AS END_DATE FROM EVENT
ORDER BY END_DATE DESC )A ) SELECT * FROM ORDEREDBRD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
