mapper.select:
WITH ORDEREDBRD AS ( SELECT A.*, ROWNUM RNUM FROM ( SELECT FRB_NO , USERS_ID , FRB_TITLE , FRB_WRITE , FRB_DATE , FRB_LNO , FRB_REP FROM FRBOARD
ORDER BY FRB_DATE DESC )A ) SELECT * FROM ORDEREDBRD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE FRBOARD
SET    FRB_LNO = FRB_LNO + 1
WHERE  FRB_NO = #{frbNo}

 	UPDATE FRBOARD
	SET    FRB_REP = 'Y'
	WHERE  FRB_NO = #{frbNo}

 	UPDATE COMMENTS
	SET    CO_REP = 'Y'
	WHERE  CO_NO = #{coNo}
