mapper.select:
WITH ORDEREDBRD AS ( SELECT A.*, ROWNUM RNUM FROM ( SELECT INTREV_NO, INTR_NO, INTERV_OVRL_EVLTN, INTERV_DIFFICULTY, INTERVIEW_TYPICAL, INTERV_PERSONNEL, INTERV_PROCESS, INTERV_QUESTION, INTERV_TIP, INTERV_PASS, TO_CHAR(INTERV_WDATE, 'YYYY.MM.DD') AS INTERV_WDATE, DECODE(INTERV_APPROVAL,'Y','승인','N','반려','W','미확인','미확인') INTERV_APPROVAL, DECODE(INTERV_REPORT,'Y','지급완료','미지급') INTERV_REPORT FROM INTERVIEW_REVIEW
ORDER BY INTERV_WDATE DESC )A ) SELECT * FROM ORDEREDBRD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
