mapper.select:
WITH ORDEREDBRD AS ( SELECT A.*, ROWNUM RNUM FROM ( SELECT A.USERS_ID , A.COMPANY_ID , B.COMPANY_NM "company.companyNm" , D.INDUSTRY_NM "comInfo.industryNm" , TO_CHAR(INTER_DATE, 'YYYY.MM.DD') AS INTER_DATE FROM INTER_COMPANY A JOIN COMPANY B ON(A.COMPANY_ID=B.COMPANY_ID) JOIN COMINFO C ON(B.COMPANY_ID=C.COMPANY_ID) JOIN INDUSTRY D ON (C.COMINFO_SECTORS = D.INDUSTRY_NO)
ORDER BY INTER_DATE DESC )A ) SELECT * FROM ORDEREDBRD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
