    rcrt_no,
    rproc_order,
    rproc_typeno,
    rproc_sdate,
    rproc_edate,
    rproc_end,
    test_no
) VALUES (
   	#{rcrtNo,jdbcType=VARCHAR}
	, #{rprocOrder,jdbcType=NUMERIC}
	, #{rprocTypeno,jdbcType=VARCHAR}
	, #{rprocSdate,jdbcType=DATE}
	, #{rprocEdate,jdbcType=DATE}
	, 'N'
	, #{testNo,jdbcType=VARCHAR}
)

mapper.select:
SELECT count (*)
FROM r_procedure
where rcrt_no = #{rcrtNo,jdbcType=VARCHAR}
