mapper.select:
WITH ORDEREDBRD AS ( SELECT A.*, ROWNUM RNUM FROM ( SELECT RCRT_NO, COMPANY_ID, RCRT_TITLE, RCRT_HIT, RCRT_KEYWORDNM , RCRT_FIELD, RCRT_PERSON, RCRT_CAREER, RCRT_CONDITION , RCRT_EDU, RCRT_LOC, RCRT_TYPE, RCRT_SALARY, RCRT_WEEK , TO_CHAR(RCRT_DATE, 'YYYY.MM.DD') AS RCRT_DATE , TO_CHAR(RCRT_SDATE, 'YYYY.MM.DD') AS RCRT_SDATE , TO_CHAR(RCRT_EDATE, 'YYYY.MM.DD') AS RCRT_EDATE , RCRT_NM, RCRT_TEL , RCRT_MAIL, RCRT_WLFR, RCRT_RECTYPE, RCRT_STIME, RCRT_ETIME, RCRT_DELETE FROM RECRUIT
ORDER BY RCRT_DATE DESC )A ) SELECT * FROM ORDEREDBRD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
