mapper.sql:
WHERE
    AP.RCRT_NO=#{variousCondition.rcrtNo}
    AND AP.RPROC_ORDER=#{variousCondition.rprocOrder}



	    		AND APROC_PASS='Y'


	    		AND  APROC_PASS='N'


	    		AND  APROC_PASS IS NULL





	    	AND USERS_GEN=#{variousCondition.usersGen}


	    	AND INSTR(USERS_NM,#{variousCondition.usersNm})>0

mapper.select:
WITH RESATTVIEW AS( SELECT DISTINCT A.APL_NO, A.RESATT_NO, RESUME_TITLE FROM APPLY A LEFT OUTER JOIN RESUME_ATTATCH R ON A.RESATT_NO=R.RESATT_NO ) SELECT T.* FROM ( SELECT ROWNUM RNUM, RN.* FROM ( SELECT AP.apl_no , rcrt_no , rproc_order , TO_CHAR(APROC_DATE, 'YYYY-MM-DD') "aprocDate" , aproc_pass , aproc_scr , USERS_ID , USERS_NM , TO_CHAR(USERS_BIR, 'YYYY-MM-DD') "usersBir" , USERS_GEN , RESUME_TITLE , RESATT_NO FROM a_procedure AP LEFT OUTER JOIN V_APPLICANTINFO B ON AP.APL_NO=B.APL_NO LEFT OUTER JOIN RESATTVIEW R ON AP.APL_NO=R.APL_NO
ORDER BY APROC_DATE DESC
ORDER BY NVL(APROC_SCR,0) DESC
) RN ) T
WHERE RNUM>=#{startRow} AND RNUM<=#{endRow}

WITH RESATTVIEW AS( SELECT APL_NO, A.RESATT_NO FROM APPLY A INNER JOIN RESUME_ATTATCH R ON A.RESATT_NO=R.RESATT_NO ) SELECT T.* FROM ( SELECT ROWNUM RNUM, RN.* FROM ( SELECT AP.apl_no , AP.rcrt_no , AP.rproc_order , TO_CHAR(APROC_DATE, 'YYYY-MM-DD') "aprocDate" , aproc_pass , aproc_scr , USERS_ID , USERS_NM , TO_CHAR(USERS_BIR, 'YYYY-MM-DD') "usersBir" , USERS_GEN , TEST_NO , RESATT_NO FROM A_PROCEDURE AP LEFT OUTER JOIN V_APPLICANTINFO B ON AP.APL_NO=B.APL_NO INNER JOIN R_PROCEDURE RP ON AP.RCRT_NO=RP.RCRT_NO AND AP.RPROC_ORDER=RP.RPROC_ORDER INNER JOIN RESATTVIEW R ON AP.APL_NO=R.APL_NO
ORDER BY APROC_DATE DESC
ORDER BY NVL(APROC_SCR,0) DESC
) RN ) T
WHERE RNUM>=#{startRow} AND RNUM<=#{endRow}

mapper.update:
UPDATE
	A_PROCEDURE
SET

		APROC_PASS=NULL


		APROC_PASS='Y'


		APROC_PASS='N'

WHERE
	APL_NO=#{aplNo}
		AND RCRT_NO=#{rcrtNo}
		AND RPROC_ORDER=#{rprocOrder}
