			, RESUME_PORTFOLIO
			, RESUME_OPEN_STATUS
			, RESUME_DATE
		) VALUES (
			#{resumeNo,jdbcType=VARCHAR}
			, #{usersId,jdbcType=VARCHAR}
			, #{resumeTitle,jdbcType=VARCHAR}
			, #{resumePortfolio,jdbcType=VARCHAR}
			, #{resumeOpenStatus,jdbcType=VARCHAR}
			, SYSDATE
		)

