mapper.select:
WITH ORDEREDBRD AS ( SELECT A.*, ROWNUM RNUM FROM ( SELECT A.USERS_ID , TO_CHAR(SCRAP_DATE, 'YYYY.MM.DD') SCRAP_DATE , A.RCRT_NO , C.COMPANY_ID "company.companyId" , C.COMPANY_NM "company.companyNm" , B.RCRT_TITLE "recruit.rcrtTitle" , B.RCRT_CAREER "recruit.rcrtCareer" , B.RCRT_EDU "recruit.rcrtEdu" , B.RCRT_TYPE "recruit.rcrtType" , B.RCRT_LOC "recruit.rcrtLoc" , TO_CHAR(RCRT_EDATE, 'MM.DD(DY)') "recruit.rcrtEdate" FROM SCRAP A JOIN RECRUIT B ON (A.RCRT_NO=B.RCRT_NO) JOIN COMPANY C ON(B.COMPANY_ID=C.COMPANY_ID)
ORDER BY SCRAP_DATE DESC )A ) SELECT * FROM ORDEREDBRD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
