mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT TEST_NO, COMPANY_ID , TEST_TITLE, TEST_TYPE, TEST_DATE FROM TEST
ORDER BY TEST_DATE DESC ) A ) B
WHERE RNUM>=#{startRow} AND RNUM<=#{endRow}

mapper.insert:

	select 'T'||LPAD(test_seq.nextval,6,'0') from dual

INSERT INTO test (
    test_no
    , company_id
    , test_title
    , test_type
    , test_date
) VALUES (
    #{testNo,jdbcType=VARCHAR}
	, #{companyId,jdbcType=VARCHAR}
	, #{testTitle,jdbcType=VARCHAR}
	, #{testType,jdbcType=VARCHAR}
	, sysdate
)

INSERT INTO test_qstn (
    test_no
    , qstn_no
    , qstn_cont
    , qstn_answer
) VALUES (
	#{testNo,jdbcType=VARCHAR}
	, #{qstnNo,jdbcType=NUMERIC}
	, #{qstnCont,jdbcType=VARCHAR}
	, #{qstnAnswer,jdbcType=VARCHAR}
)

INSERT INTO test_item (
    test_no
    , qstn_no
    , item_no
    , item_cont
) VALUES (
	#{testNo,jdbcType=VARCHAR}
	, #{qstnNo,jdbcType=NUMERIC}
	, #{itemNo,jdbcType=NUMERIC}
	, #{itemCont,jdbcType=VARCHAR}
)

mapper.update:
UPDATE test
SET
    test_title = #{testTitle}
WHERE
    test_no = #{testNo}

UPDATE test_qstn
SET
	qstn_cont = #{qstnCont}
    ,qstn_answer = #{qstnAnswer}
WHERE
    test_no = #{testNo}
    AND qstn_no = #{qstnNo}

UPDATE test_item
SET
    item_cont = #{itemCont}
WHERE
    test_no = #{testNo}
    AND qstn_no = #{qstnNo}
    AND item_no = #{itemNo}
